  seo_keywords = ARRAY['manicure japonski', 'manicure japonski bialystok', 'P.Shine manicure', 'manicure japonski efekty', 'naturalny manicure', 'manicure odzywczy', 'manicure bez lakieru'],
  reading_time_minutes = 7,
