  seo_keywords = ARRAY['manicure hybrydowy pielegnacja', 'hybrydy bialystok', 'jak przedluzyc trwalosc hybrydy', 'dlaczego hybryda odpada', 'pielegnacja paznokci po hybrydzie', 'hybrydowy manicure ile trzyma', 'manicure bialystok'],
  reading_time_minutes = 6,
